    Recipe("id265", "Stir-Fry Sauce (Sweet and Sour)", "", 0, 0, 0, 4, "", 0, 0, 0, 0, 0, 0, 0, "Mix all ingredients.", ["3/4 cup chicken broth", "2 tsp cornstarch", "1/4 cup ketchup", "2 tsp soy sauce", "3 tbsp rice vinegar", "1/4 cup sugar", "1/2 tsp salt", "1 tsp sesame oil"],
        ["stir-fry", "components", "sauce"]),
    Recipe("id268", "Stir-Fry Sauce (Clear)", "", 0, 0, 0, 4, "", 0, 0, 0, 0, 0, 0, 0, "Mix all ingredients.", ["3/4 cup chicken broth", "1 tbsp cornstarch", "2 tbsp rice wine", "1/2 tsp sesame oil", "1 tsp salt", "1/2 tsp sugar"],
